        AND product_price = #{condition.productPrice}

UPDATE zgk_order
user_id = #{userId}, product_id = #{productId}, status = #{status}, order_no = #{orderNo}, channle = #{channle}, create_date = #{createDate}, update_date = #{updateDate}, state = #{state}, amount = #{amount}, product_number = #{productNumber}, product_price = #{productPrice},
WHERE id = #{id}

mapper.delete:
DELETE FROM zgk_order


        AND id = #{id}


