select user_id, status,order_no, channle,create_date, update_date,state,goods_address,product_price, department_name,department_phone, goods_count as goodsCount ,
unit_price,handle_state,product_type
from zgk_order where user_id=#{userId} order by create_date desc limit ${pageNo},${pageSize} ;
