UPDATE zgk_order_statements
order_no = #{orderNo}, statement_no = #{statementNo}, status = #{status}, create_date = #{createDate}, update_date = #{updateDate}, creater_id = #{createrId}, state = #{state}, amount = #{amount},
WHERE id = #{id}

mapper.delete:
DELETE FROM zgk_order_statements


        AND id = #{id}


        AND order_no = #{orderNo}


        AND statement_no = #{statementNo}


        AND status = #{status}


        AND create_date = #{createDate}


        AND update_date = #{updateDate}


        AND creater_id = #{createrId}


        AND state = #{state}


        AND amount = #{amount}

