mapper.select:
SELECT acc.id, info.name, info.icon, acc.account, acc.password, acc.status, acc.userType, exam.isReported, exam.isSurvey, vip.status as vipStatus FROM zgk_user_account acc left join zgk_user_vip vip on acc.id = vip.id left join zgk_user_info info on acc.id = info.id left join zgk_user_exam exam on acc.id = exam.id
acc.userType = 0
and info.token = #{token}
and acc.id = #{id}
and acc.account = #{account}
and acc.areaId = #{areaId}

SELECT
  COUNT(acc.id)
FROM zgk_user_account acc
left join zgk_user_vip vip on acc.id = vip.id
left join zgk_user_info info on acc.id = info.id

    acc.userType = 0

        and info.token = #{token}


        and acc.id = #{id}


        and acc.account = #{account}


        and acc.areaId = #{areaId}
