mapper.update:
UPDATE zgk_user_info


        token = #{update.token},


        name = #{update.name},


        icon = #{update.icon},


        provinceId = #{update.provinceId},


        cityId = #{update.cityId},


        countyId = #{update.countyId},


        schoolName = #{update.schoolName},


        birthdayDate = #{update.birthdayDate},


        sex = #{update.sex},


        subjectType = #{update.subjectType},


        mail = #{update.mail},


        qq = #{update.qq},





