        AND sex = #{condition.sex}


        AND subjectType = #{condition.subjectType}


        AND mail = #{condition.mail}


        AND qq = #{condition.qq}

UPDATE zgk_user_info
token = #{token}, name = #{name}, icon = #{icon}, provinceId = #{provinceId}, cityId = #{cityId}, countyId = #{countyId}, schoolName = #{schoolName}, birthdayDate = #{birthdayDate}, sex = #{sex}, subjectType = #{subjectType}, mail = #{mail}, qq = #{qq},
WHERE id = #{id}

